class Alien:
    def __init__(self, x, y):
        self.vel = 0.4

        self.x = x
        self.y = y
        self.image = ALIEN_IMG
        self.mask = pg.mask.from_surface(self.image)

    def draw(self, window):
        window.blit(self.image, (self.x, self.y))

    def move(self):
        self.y += self.vel
        self.vel += 0.0001

